// variant of https://shadertoy.com/view/4sdBD8

void mainImage(out vec4 O, vec2 U) {

    float t = iTime,l,d, r0=200.,r1=20., a;
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution, c,
          D = normalize(vec3(.3*(U+U-q.xy)/q.y, -1)),    // ray direction
          p = q-q; // 90./q;                  // start position
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )// marching point along ray 
        q = p,
        q.xz *= R, q.yz *= R,                 // rotation
        c = q, c.z+=r0, a = atan(c.y,c.z), 
        q.z = length(c.zy)-r0, q.y = r0*a, q.x = mod(q.x-a*r0/6.3,r0)-r0/2.,// large helix
        c = q, c.x+=r1, a = atan(c.z,c.x), 
        q.x = length(c.xz)-r1, q.z = r1*a, q.y = mod(q.y-a*r1/6.3,r1)-r1/2.,// medium helix
        l = length(q.xy), d = sin(atan(q.y,q.x)-q.z),           // sin: double
                      //  d = mod(atan(q.y,q.x)-q.z,6.28)-3.14, // mod: single
        t = length(vec2(l-4.,d)) - .5,        // double-spring
        p += t*D;                             // step forward = dist to obj
}
