void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv.x-=0.3;
    vec2 center = vec2(0.5);

    float d = 0.1;
    d += tan(fragCoord.x+fragCoord.y+iTime)*0.01;
    d += sin(7.0*uv.x-uv.y*7.0+iTime)*0.1;
    uv.x += sin(uv.y)*d;
    uv.y += cos(uv.x)*d;

    float e = distance(center,uv);
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float g = e > 0.3 ? 0.0 : 1.0;
    float f = uv.y*0.5;
    float k = smoothstep(0.2,0.2+f,e);

    fragColor = vec4(k);
}
