const float SCALE_X = 5.;
const float SCALE_Y = 10.;

float plotUnscaled(in float axis, in float y)
{
    float col = step(y - 0.01, axis);
	col -= step(y + 0.01, axis);
    col = 1. - col;
    return col;
}


float plot(in float axis, in float y)
{
    float col = step(y - 0.01 * SCALE_Y, axis);
	col -= step(y + 0.01 * SCALE_Y, axis);
    col = 1. - col;

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // center coord
    uv = uv * 2. - 1.;

    // undistort x axis
    uv.x *= iResolution.x/iResolution.y;

    // draw x and y axes
    float col = plotUnscaled(uv.y, 0.) * plotUnscaled(uv.x, 0.);
    col += 0.9;

    // scale uv
    uv *= vec2(SCALE_X, SCALE_Y);

    // --------- FUNC --------------
    // -----------------------------
    float res = exp(uv.x);
    // -----------------------------
    // -----------------------------

    col *= plot(uv.y, res);
    //col *= plot(uv.y, 1. / res);
    //col *= plot(uv.y, - res);
    //col *= plot(uv.y, 1. / - res);

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}
