void circle(){}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    	vec2 p = fragCoord.xy / iResolution.y ;
    //Position animation 
    vec2 q = p - vec2(.8-cos(iTime*2.0)/5.0,0.5-sin(iTime*2.1)/5.0);
    // Color
     vec3 col = mix(vec3(0.4, 0.6,0.1),vec3(1.0, 0.8,0.8),tan(p.x*cos(iTime*4.0)));


   //the circle - make sure the aspect ratio is fixed.
    float r = .2;
    col *= smoothstep(r+.1, r+.01, length(q));

	fragColor = vec4(col,1.0);
}


