#define r iResolution
#define PI 3.1415926
#define gt iChannelTime[0] * .5
#define gt2 iChannelTime[0]
#define cht mod(ceil(gt * 1.075)

float l( in vec2 p, in float x1, in float y1, in float x2, in float y2, float size )
{
    size = 1. - size;
    vec2 a = vec2( x1, y1 );
    vec2 b = vec2( x2, y2 );
    vec2 pa = -p - a;
    vec2 ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0., 1. );
    float d = length( pa - ba*h );

    return clamp(((1. - d)-size)*100., 0., 1.);
}

float char_N(vec2 p)
{
    p *= -1.;
    float res = 0.;
    res=max(res,l(p,.1,.21,-.02,.21, .015));
    res=max(res,l(p,-.02,.21,-.08,.16, .0125));

    res=max(res,l(p,-.02,.0,-.06,-.02, .015));
    res=max(res,l(p,-.06,-.02,-.08,.0, .0125));

    res=max(res,l(p,.22,.21,.28,.21, .0125));

    res=max(res,l(p,.1,.0,.14,.0, .0125));
    //main part
    res=max(res,l(p,.1,.21,-.02,.0, .015));
    res=max(res,l(p,.1,.21,.1,.0, .015));
    res=max(res,l(p,.22,.21,.1,.0, .015));
	///////////

    return res;
}

float circle (vec2 uv, vec2 center, float rad, float width) 
{

    vec2 diff = center-uv;
    float len = length(diff);

    float circle = smoothstep(rad-width, rad, len) - smoothstep(rad, rad+width, len);
    return circle;
}

void mainImage( out vec4 o, in vec2 f )
{
	vec2 p = 2. * (2.*f - r.xy)/ min(r.x,r.y);

    vec3 col = vec3(0); //bkg

    if ( cht , 4.) == 0. ) col = vec3( .5 );
    else if ( cht, 3.) == 0. ) col = vec3( .7,.6,.8);
    else if ( cht, 2.) == 0. ) col = vec3( .6,.7,.5);
    else if ( cht, 1.) == 0. ) col = vec3( .8,.6,.7);

    if (abs(p.y) > 1.5) col *= 0.9;

    for (int i = -5; i <= 5; i++) //hrt
    {
        float wave = sin(gt) * 0.25 + .75;

        vec2 cntr = p - vec2(float(i) * wave * 2. + sin(gt2), abs(sin(gt * 6.74))+.5);

        vec2 q = cntr;
        float c = cos(atan(q.x, q.y) * 7. + 50. * sin(gt));
        c *= wave;
        c = 1. - smoothstep(c, c + 0.01, length(q));
        col = mix(col, vec3(1.)*c, c);



        q = cntr-vec2(0, 0.25 * wave);
        float t = abs(atan(q.x * .75,q.y)/PI);
        c  = (13.0*t - 22.0*t*t + 10.0*t*t*t)/(6.0-5.0*t); // iq's function
        c *= 1.25 * wave;
        c =  1. - smoothstep( c, c+0.01, length( q ) );
        col = mix(col, vec3(1.,0.,0.) * c, c);
    }

    // Neil Tennant ( made from a screenshot manually :-D )
    vec2 ccntr = vec2(cos(gt*.5)*5., -1. + abs(sin(gt * 6.74)));
    vec2 hs = p - ccntr - vec2(0., 0.925 + .05 * abs(cos(gt * 6.74))); // headshift
    float circ = 0.;


    float line = 0.;
    	//head background (black)
        line = l(hs, .0, .1, .0, -.25, .14);
    col = mix(col, vec3(0) * line, line);//neck
    	circ = 1. - smoothstep(0.16, 0.17, length(hs - vec2(0,.205)));
    col = mix(col, vec3(0) * circ, circ);//face

    	circ = 1. - smoothstep(0.145, 0.15, length(hs - vec2(0,.205)));
    //col = mix(col, vec3(1) * circ, circ);//face
    	line = max(circ,l(hs, .0, .1, .0, -.25, .125));
    col = mix(col, vec3(1) * line, line);//neck


    	// face attributes
    	// hair
    	line = l(hs,.06,-.34,.04,-.33, .0075);
    	line = max(line, l(hs,.04,-.33, -.04,-.33, 0.0075));

    	// temples
    	line = max(line, l(hs,.12,-.275,.11,-.175, 0.0075));
    	line = max(line, l(hs,-.12,-.275,-.11,-.175, 0.0075));

    	// brows
    	line = max(line, l(hs,.09,-.235, .0855,-.26, 0.01));
    	line = max(line, l(hs,.085,-.26,.03,-.26, .012));
    	line = max(line, l(hs,-.085,-.26,-.03,-.26, .012));

    	// eyes
    	line = max(line, l(hs,.05,-.23,.05,-.23, .0175));
    	line = max(line, l(hs,-.05,-.23,-.05,-.23, .0175));

    	// nose
    	line = max(line, l(hs,.009,-.165,-.009,-.165, .0075));
    	line = max(line, l(hs,.009,-.165,.01,-.1675, .0075));
    	line = max(line, l(hs,-.009,-.165,-.01,-.1675, .0075));

    	// lips
    	line = max(line, l(hs, .02,-.115, -.02,-.115, .012));
    	line = max(line, l(hs, .015,-.095,-.015,-.095, .0075));

    	// chin
    	line = max(line, l(hs, 0.02, -.04, -.02, -.04, .008));
    	line = max(line, l(hs, 0.06, -.07, .08, -.11, 0.005));
    	line = max(line, l(hs, -0.06, -.07, -.08, -.11, 0.005));
    col = mix(col, vec3(0) * line, line);

    // circle
    	circ = 1. - smoothstep(0.84, 0.855, length(p - ccntr));
    col = mix(col, vec3(1.) * circ, circ);//white

    	circ = circle(p, ccntr, .84, 0.02);
    col = mix(col, vec3(0.) * circ, circ);//black outer

    // circle / pattern
        vec2 pcntr = p - ccntr;
        float ptn = sin(atan(pcntr.x, pcntr.y) * 75. + gt);
        ptn *= 0.84;
        ptn = 1. - smoothstep(ptn, ptn + 0.015, length(pcntr));
    col = mix(col, vec3(0.2)*ptn, ptn);

    	circ = 1. - smoothstep(0.7, 0.715, length(p - ccntr));
    col = mix(col, vec3(1.) * circ, circ);//inner white
    //////////////////////////////////////////////////////

    	circ = circle(p, ccntr, .7, 0.02);
    col = mix(col, vec3(0.2) * circ, circ);//black inner

    // circle / pattern
        ptn = cos(atan(pcntr.x, pcntr.y) * 8.);
        ptn *= 0.4;
        ptn = 1. - smoothstep(ptn, ptn + 0.415, length(pcntr));
    col = mix(col, vec3(.4)*ptn, ptn);

    	circ = 1. - smoothstep(0.3, 0.315, length(p - ccntr));
    col = mix(col, vec3(1.) * circ, circ);//inner white
    //////////////////////////////////////////////////////

    	circ = circle(p, ccntr, .3, 0.06);
    col = mix(col, vec3(0.2) * circ, circ);//black pupil with N
	// N
    	float chr = char_N(pcntr + vec2(0.1));
	col = mix(col, vec3(.1) * chr, chr);

    // sweater
        line = 0.;
    	line = max(line,l(pcntr - vec2(0., 0.925),-.1,.04,.1,.04, .05));
    	line = max(line,l(pcntr - vec2(0., 0.925),-.2,.1,.2,.1, .05));
    col = mix(col, vec3(.0) * line, line);

    o = vec4(col, 1.);

}
