void mainImage( out vec4 f, in vec2 g )
{
    f = texture(iChannel0, g / iResolution.xy);
}

void mainImage( out vec4 f, in vec2 g )
{
    f = vec4(.8,.2,.5,1);
    vec2 s = iResolution.xy;

    vec2 n = vec2(100.,5.);
    if (iMouse.z>0.)
        n *= (iMouse.xy/s);

    n = max(n, vec2(0.01));

	vec4 h = texture(iChannel0, g / s * 0.995); // the magie is the 0.995 of s

    g = (g+g-s)/s.y*n.y;

    vec2 k = vec2(1.6,0) + iDate.w,a;
    float m;


    for (float i=0.;i<100.;i++)
    {   
    	if ( i > n.x) break;
        a = g - sin(k + 6.28218/n.x*i);
        m += 0.01/dot(a,a);
    }

    f = m * 0.03 + h * 0.97 + step(h, f) * 0.01;
}
