vec2 SCREEN1 = vec2(256.0,192.0);
float PI = 3.1416;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = floor(fragCoord.xy / iResolution.xy * SCREEN1) / iResolution.xy;
    fragColor = texture(iChannel0,uv)*0.5;
    uv.x-= .1/iResolution.x;
    fragColor += texture(iChannel0,uv);
    uv.x+= 0.2/iResolution.x;
    fragColor += texture(iChannel0,uv)*0.5;
    fragColor = (0.3+fragColor); // gray offset

    // 576 scanlines in that time on PAL 50Hz interlaced
    fragColor *= pow(max(0.0,sin(fragCoord.y / iResolution.y * 576.0 + float(iFrame) * PI)),0.7);

    // fragColor *=(0.8*abs(sin((floor(fragCoord.y)/iResolution.y)*PI*128.0 //CRT lines, i had a BW screen
    //             +mod(float(iFrame),2.0)*PI*.5))); //interlaced
}

vec2 SCREEN1 = vec2(256.0,192.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = floor(fragCoord.xy-.5);// / iResolution.xy;
    // uv *= SCREEN1;
    // uv = floor(uv);

    float I = float(iFrame);

    vec2 PSET = floor(vec2(128.0+128.0*sin(0.020*I),
                            96.0+ 96.0*cos(0.022*I)));

    if (PSET.x==uv.x && PSET.y==uv.y)
       fragColor = vec4(1.0);
    else
       fragColor = texture(iChannel0,fragCoord.xy / iResolution.xy);
}
