float calGap(float a, float b, float x) {
    if(x>=a && x<=b) {
    	//return 1.0;
        return smoothstep(a, b, x);
    } else {
    	return 0.0;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float column = 10.0;

    float c = abs(sin(iTime / 3.0));

    float gap = 0.1;

    float f = fract(uv.x * column);

    float cr = calGap(c, c+gap, f);


    vec3 col = vec3(cr);

    fragColor = vec4(col,1.0);
}
