/**

#define D(t) smoothstep(.1,-.1,length( vec2(.4*cos(t),-.7) - U ) - .2 )
#define B(V) O = V.a > O.a ? V : O

void mainImage( out vec4 O, vec2 U )
{
    O = T(U+vec2(0,-1));
    vec2 R = iResolution.xy;
    U = ( U+U - R ) / R.y;
    float t = iTime;

    B( D(t) * vec4( 1,0,0, sin(2.*t)+1. ) );
    t += 2.1;
    B( D(t) * vec4( 0,1,0, sin(2.*t)+1. ) );
    t += 2.1;
    B( D(t) * vec4( 0,0,1, sin(2.*t)+1. ) );
}

