float PI = 3.14159265359;
float saw(float x)
{
    return acos(cos(x))/3.14;
}
vec2 saw(vec2 x)
{
    return acos(cos(x))/3.14;
}

vec2 spiral(vec2 uv)
{
    float turns = 4.0+float(int(4.0*saw(iTime*.321)));
    float r = pow(log(length(uv)+1.)+1.0, 1.25+.25*sin(iTime*.7654))+iTime;
    float theta = atan(uv.y, uv.x)*turns+r*PI;
    return vec2(saw(r*PI+theta/turns*float(int(1.0*saw(iTime*.543)))), saw(theta+iTime*1.1));
}
vec2 spiral2(vec2 uv)
{
    float turns = 4.0+float(int(4.0*saw(iTime*.321)));
    float r = pow(log(length(uv)+1.)+1.0, 1.25+.25*sin(iTime*.7654))+iTime;
    float theta = atan(uv.y, uv.x)*turns+r*PI;
    return vec2(saw(r*PI+theta/turns*float(int(1.0*saw(iTime*.543)))), saw(theta+iTime*1.1));
}
vec4 draw(vec2 uv)
{
	if(abs(abs(saw(uv.x*(1.5+sin(iTime*.654321))*PI+iTime*.7654321)*2.0-1.0)-abs(uv.y)) < .5)
		return vec4(1.0-abs(abs(saw(uv.x*(1.5+sin(iTime*.654321))*PI+iTime*.7654321)*2.0-1.0)-abs(uv.y))/.5)*uv.x;
	else
		return vec4(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = (22.0+11.0*sin(iTime*.1234))*PI;
    vec2 uv = fragCoord/iResolution.xy;
    uv = spiral(uv*scale-scale/2.0);
    vec4 screen = draw(uv)*clamp(saw(iTime*1.45678), 0.0, 1.0);


    scale = (40.0+20.0*sin(iTime*.234))*PI;
    uv = spiral(uv*scale-scale/2.0);
    screen += draw(uv)*clamp(saw(iTime*1.2345678), 0.0, 1.0);

    screen.x *= 2.0*PI;
    fragColor = clamp(screen.x, 0.0, 1.0)*vec4(vec3(sin(iTime*5.0+0.0+screen.x),
                                                       sin(iTime*5.0+4.0*PI/3.0+screen.x),
                                                       sin(iTime*5.0+2.0*PI/3.0+screen.x))*.5+.5,
                                                  1.0);
}
