void mainImage(out vec4 k,vec2 p)
{
#define f length(length(k*=k)-p)
//    p = p.yx;						// rotate 90 degrees (fullscreen)
    p = (p.x>4e2 ? p.yy : p)/150.;	// ortho project fade stripe / size
    vec4 t = sin(iDate); 			// temporal component
    k = t.wwww;
    k += vec4(f,f,f,0);

//    k -= k.r;						// fade variations
//    k -= k.g;
//    k -= k.b;
//    k -= k.a;

	k = t.w<0. ? 1.-k : k;			// negative toggle (CMY on WHT / RGB on BLK)



