// original code from my good buddy cubby208 https://www.shadertoy.com/view/XsySRw

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float size = 1.0 - smoothstep(0.0, iResolution.x, iMouse.x);
    float hardness = 1.0 - smoothstep(0.0, iResolution.y, iMouse.y);

	vec2 trans = 2.0 * uv - 1.0;
    float part = -dot(trans, trans) + 1.0;

    float calc = smoothstep(size, size + hardness, part);

    fragColor = vec4(calc);
}
