void ACESFilm( inout vec3 x ){
	x = clamp(.14 + (x*(2.51*x+.03)) /
        			(x*(2.43*x+.59)), 0., 1.);
}

void mainImage(out vec4 k,vec2 p)
{    
    // 112 chars without comment bug, 
    // ... should likely be longer with repeated #define use
	#define f length(length(k*=k)-p/2e2)
    k /= abs(k = sin(iDate.wwww))*vec4(f,f,f,1);

    // granito's tonemapping on temporal toggle
    if(mod(iDate.w,6.28)>3.14) ACESFilm(k.rgb);
}
