// noise colors example #8; played by gigatron 


float rand(vec2 n) { 
    return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p){
    vec2 ip = floor(p);
    vec2 u = fract(p);
    u = u*u*(3.0-2.0*u);

    float res = mix(
        mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
        mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
    return res*res;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t=iTime;
	vec3 t1 = texture(iChannel0,uv-noise(uv-vec2(0.00+t*0.2,0.00))).rgb;

    vec3 t2 = texture(iChannel0,uv-noise(uv-vec2(0.01+t*0.2,0.00))).rgb;
    vec3 t3 = texture(iChannel0,uv-noise(uv-vec2(0.02+t*0.2,0.00))).rgb;

    t1.r,t1.g,t1.b =sin(t+t);


	fragColor = vec4(t1+(t2/2.0)+(t3/1.5), 1.0)*0.5;
}
