void mainImage(out vec4 fragColor, in vec2 fragCoord){
  fragColor = fract(
    radians(
      mod(
        vec4(
          (gl_FragCoord.y / iResolution.y),
          1.8,
          1.7,
          0.0
        ),
        mod(
          sqrt(
            abs(
              pow(
                vec4(
                  0.3,
                  (0.0 * 0.8),
                  ((gl_FragCoord.y / iResolution.y) * reflect(((gl_FragCoord.x / iResolution.x) / 1.8), (0.3 + 1.8))),
                  1.9
                ),
                degrees(
                  vec4(
                    0.7,
                    1.9,
                    cos(iTime),
                    1.0
                  )
                )
              )
            )
          ),
          log(
            fract(
              vec4(
                0.5,
                iTime,
                0.2,
                0.1
              )
            )
          )
        )
      )
    )
  );
}
