const vec2 freq = vec2(1.0);
const vec2 amp = vec2(0.1);

const float c_pi = 3.1415;
const float c_2pi = 2.0*c_pi;
const float c_2pi_i = 1.0/(2.0*c_pi);
const float c_pi_2 = c_pi / 2.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 perturb = vec2(0.0);
    float rad;
    vec3 color;
    float startRad = fract(iTime)*c_2pi;

	rad = (uv.x + uv.y - 1.0 + startRad) * freq.x;

    rad = fract(rad * c_2pi_i);
    rad = rad * c_2pi;

    if(rad>c_pi) rad = rad - c_2pi;
    if(rad<-c_pi) rad = rad + c_2pi;

    if(rad > c_pi_2) rad = c_pi - rad;
    if(rad < -c_pi_2) rad = -c_pi - rad;

    perturb.x = (rad-(rad*rad*rad / 6.0)) * amp.x;

    rad = (uv.x - uv.y + startRad) * freq.y;
    rad = fract(rad * c_2pi_i);
    rad = rad * c_2pi;

    if(rad>c_pi) rad = rad - c_2pi;
    if(rad<-c_pi) rad = rad + c_2pi;

    if(rad > c_pi_2) rad = c_pi - rad;
    if(rad < -c_pi_2) rad = -c_pi - rad;

        perturb.y = (rad-(rad*rad*rad / 6.0)) * amp.y;

    vec3 col = vec3(texture(iChannel0, uv + perturb).r);
    // Output to screen
    fragColor = vec4(col,1.0);
}
