vec4 color( in vec2 fragCoord, in vec2 centerCoord )
{

    float dist = length(centerCoord - fragCoord);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(vec3(dist/10.0) - iTime * vec3(11.0,3.0,7.0) );

    float cutoff = 40.0;
    float d = min(1.0, max(cutoff/dist, .3));
    return vec4(col*d, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor  =
    	color(fragCoord, iResolution.xy/2.0 + 30.0 * vec2(sin(iTime), cos(iTime)))
        -color(fragCoord, iMouse.xy);
}
