void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float modulo = 10.0;
    fragCoord = mod(fragCoord, modulo) - modulo/2.0;

    vec3 col = vec3(0,1,1);

    float radius = 30.0;
    float thickness = 10.0;

	float circle = dot(fragCoord, fragCoord);


    if (abs(circle - radius) < thickness)
    {
        col = vec3(0,0,0);
    }







    fragColor = vec4(col,1.0);
}
