#define MOD .02
#define BLUE vec4(.11, .15, .2, 1.)
#define WHITE vec4(1., .98, .9, 1.)

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
	vec2 pos = (fragCoord -.5 * lM) / lM.x;

    float time = iTime * .001;
	float x = pos.x * 1.2 + time * 2.;
	x = .1 * sin(10. * cos(x / 2.) + cos((12. + sin(time * 7.)) * x) / 4. - sin((-28. + cos(time * 9.)) * x) / 9.) - .2 + mod(time, .6);
	if (pos.y < x) fragColor = mix(BLUE, WHITE, smoothstep(x, x - .005, pos.y));
	else {
		float size = MOD * .2;
		float decal = floor(pos.x / MOD);
		float pseudoRand = sin(decal * MOD * 100000.);
		float amplitude = pseudoRand * .5 + .5;

		pos.x = mod(pos.x + .5, MOD) - MOD / 2. + sin(iTime * 5. + decal) * (MOD / 2. - size) * amplitude;

		pos.y += iTime * (.2 + .2 * (sin(decal) * .5 + .5));
		pos.y = mod(pos.y + .5 + pseudoRand, .6) - size;

		size /= 2.;
		size += size * amplitude;

		float dist = length(pos);
		fragColor = length(pos) < size ? mix(BLUE, WHITE, smoothstep(size, size * .4, dist)) : BLUE;
	}
}
