void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float NUM_LINES = iResolution.y/2.0;

    vec2 uv = fragCoord.xy / iResolution.xy;

    float nL = floor(uv.y * NUM_LINES);
    float x = mod(nL, 2.0);
    float posInTrans = (nL * 1.0/NUM_LINES)*0.5 + x * 0.5;


    float trans = (1.0)*0.5;
    trans = step(posInTrans, trans);

    vec3 src = texture(iChannel0, uv).rgb;
    vec3 dest = texture(iChannel0, uv + vec2(0.01,0.0)).rgb;

	fragColor.rgb =  mix(src, dest, trans);
}
