float v(float v,int m)
{
  float i=floor(v),s=v-i;
  vec2 a=sin(vec2((ivec2(i++,i)*51^m)*(m+5)%13))*vec2(s,s-1.);
  return mix(a.x,a.y,(3.-s-s)*s*s);
}
void mainImage(out vec4 a,vec2 m)
{
  vec2 s=iResolution.xy,i=m-s*.5;
  a=vec4(0);
  for(int c;++c<33;)
    {
      float y=iTime*.8+1.5,l=v(y,c*3),x=sin(l),z=cos(l),I=min(s.x,s.y)*.1;
      vec2 f=(vec2(v(y,c),v(y,c*2))*s+i)*mat2(z,x,-x,z)/I;
      a.rgb=max(a.rgb,clamp(1.-min(length(max(abs((f+vec2(0,sqrt(.5)))*mat2(1,1,-1,1))-sqrt(2.),0.))*sqrt(.5),length(vec2(f.y,abs(f.x)-sqrt(.5)))-1.)*I,0.,1.)*vec3(.9,.2,.1)+v(.5,c));
    }
  a.b+=.07;
}
