// Fork of "Square repeating" by Zarhead. https://shadertoy.com/view/Mf23Dm
// 2024-01-06 20:30:35

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

vec3 palette( in float t )
{

    vec3 a = vec3(0.500, 0.500, 0.500);
    vec3 b = vec3(0.500, 0.500, 0.500);
    vec3 c = vec3(0.502,0.502,0.502);
    vec3 d = vec3(0.50, 0.20, 0.25);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;

    vec3 finalColour = vec3(0.0);

    for( float i = 0.0; i<3.0; i++){

        uv = fract(uv * 1.5) - .5;

        vec2 boxDimensions = vec2(0.5, 0.5);

        float d = sdBox (uv, boxDimensions);
        vec3 colour = palette(d + iTime*.2);

        //Transformations
        d =  sin(d*21. - iTime)/3.;
        d= abs(d);

        finalColour += colour * d;
    }

    fragColor = vec4(finalColour, 1.0);
}
