// 2024年8月31日
// 第4种五边形平铺

float dln(in vec2 a, in vec2 b, in vec2 p) {
        b -= a;
        p -= a;
        float h = clamp(dot(p, b) / dot(b, b), 0., 1.);
        return length(p - b * h);
}

void mainImage(out vec4 O, vec2 U) {
        O = vec4(.5);
        vec2 R = iResolution.xy,
                u = 3. * (U + U - R) / R.y;
        //if(length(floor(u +.5))>1.5)return ;
        vec2 f = u - floor(u + .5),
                s = sign(f);
        f = abs(f);
        if ((s.x * s.y) < 0.) f = f.yx;
        vec2 x = clamp(vec2(.25) + .2 * vec2(cos(iTime * .3), sin(iTime * 1.3)), 0., .5); // 
        if (iMouse.x > R.x * .55) x = vec2(.4, .2);
        if (x.x < x.y) x = x.yx;
        vec2 a = vec2(0),
                b = vec2(.5),
                c = vec2(1, 0) - x,
                e = x.yx * vec2(-1, 1),
                g = abs(c.yx);
        float d = 1e8;
        d = min(d, dln(x, a, f));
        d = min(d, dln(x, b, f));
        d = min(d, dln(x, c, f));
        //d= min(d, dln(x,c,f.yx*vec2(1,-1)));
        d = min(d, dln(e, -e, f));
        d = min(d, dln(g, 1. - g, f));
        d = min(d, dln(g, e, f));
        O = .8 - vec4(.006 / d);
}
