#define time iTime
#define resolution iResolution.xy
#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 dir = normalize(vec3((cos(iTime)*-.5)*-4.,(sin(iTime)*-.5)*4.,1.0));
	vec3 left = normalize(cross(dir,vec3(0,1,0)));
	vec3 up = cross(dir,left);
    vec4 O=fragColor;
     vec2 C=fragCoord;
	 O=vec4(0);
    vec3 p,q,r=iResolution,
    d2=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(1),H(g*.1),.8)*1./e/8e3
    )
    {
        p=g*d2;


        a=30.;
        p=mod(p-a,a*2.)-a;
        s=3.;

        for(int i=0;i++<10;){

            p=0.1-abs(p);
            p.x<p.z?p=p.zyx:p;
            p.z<p.x?p=p.xzy:p;
            s*=e=1.50*1.1;

            p=abs(p)*e-
                vec3(
                    5.*3.,
                    120,
                    10.*5.
                 );
         }
         g+=e=length(p.xyy)/s;
         g+=e=length(p.xzy)/s;
    }
	vec3 pos = -dir*10.+O.xyz;

	vec2 screen = (gl_FragCoord.xy-resolution*.5)/resolution.x;

	vec3 ray = normalize(dir+left*screen.x-up*screen.y);

	float dsum = 0.;
	float d;

	for (int i = 0; i < 75; i++) {
		d = length(max(max(abs(fract(pos+O.xyz)-.5)-.3,0.), abs(pos+O.xyz)-2.0));
		dsum += d;
		pos  += d*ray;
	}


	fragColor = vec4(1.0-vec3(dsum)*0.08,1.);
    fragColor+=O+O+O;

}
