float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

vec3 palette( in float t )
{

    vec3 a = vec3(0.778, 0.500, 0.388);
    vec3 b = vec3(0.655, 0.405, -0.471);
    vec3 c = vec3(0.558, 1.558, -0.172);
    vec3 d = vec3(0.000, -0.362, 0.667);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 finalColour = vec3(0.0);

    for( float i = 0.0; i<3.0; i++){

        uv = fract(uv * 1.5) - .5;

        vec2 boxDimensions = vec2(0.5, 0.5);

        float d = sdBox (uv, boxDimensions);
        vec3 colour = palette(d + iTime*.2);

        //Transformations
        d = 1. / sin(d*8. - iTime)/8.;
        d= abs(d);
        d = pow(d, 1.);

        finalColour += colour * d;
    }

    fragColor = vec4(finalColour, 1.0);
}
