void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    float radius = .03;
    float effect = .3;// 1 = max, 0 = min, i recommend keeping it below .4
    bool isConcave = false;


     vec2 uv = fragCoord/iResolution.x;
    float aspect = iResolution.x/iResolution.y;
    float xDist = uv.x-(iMouse.x/iResolution.x);
    float yDist = (uv.y-iMouse.y/(iResolution.y*aspect));
    float dist = sqrt(pow(xDist,2.)+pow(yDist,2.));

    if(isConcave)
    {
    xDist *= -1.;
    yDist *= -1.;
    }

    if(iFrame == 0)
    {
    fragColor.rgb = vec3(.5,.5,1);
    }
    else if(dist < radius)
    {
    fragColor.r = .5+(.5 * (xDist/radius))*effect;
    fragColor.g = .5+(.5 * (yDist/radius))*effect;
    fragColor.b = 1.-(.5 * (dist/radius))*effect;
    }
    else
    discard;
}
