#define ROTATION_AMPLITUDE .6
#define ROTATION_VELOCITY 1.2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 grass_uv = uv * 1.5;

    vec3 grass_noise = texture(iChannel0, uv / 20.).rgb;
    float grass_intensity = grass_noise.r;

    float rotation_factor = sin((1. - grass_intensity) * ROTATION_AMPLITUDE) * sin(iTime * ROTATION_VELOCITY);
    vec2 rotation = vec2(cos(rotation_factor), sin(rotation_factor));

    float grass_height = 0.;
    for (int i = 0; i < 12; i++) {
        grass_uv += .0025 * rotation;
        grass_height += texture(iChannel0, grass_uv).r * .1;
    }
    grass_height = clamp(grass_height, 0., 1.);

    vec3 grass_color = mix(
        (vec3(85., 109., 50.) / 255.) * grass_height,
        (vec3(65., 76., 28.) / 255.) * grass_height * .7,
        grass_intensity
    );
    grass_color = mix(
        grass_color,
        (vec3(127., 99., 33.) / 255.),
        grass_noise.r * .04
    );
    grass_color = mix(
        grass_color,
        (vec3(125., 67., 30.) / 255.),
        grass_noise.b * .06
    );


    fragColor = vec4(grass_color * 3.,1.);
}
