#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,h; vec3 p,q=vec3(-.6),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    //p.z-=.5;p.xy-=.3;
    p=R3D(iTime);

    //for(j=0.;j++<2.;)
    p=abs(abs(p*mix(p,q,q+.7))-.05)-.04,
    h=clamp(dot(p,q)/dot(q,q),-1.,1.);
    d=length(p-q*h)-.004;
    e-=d;
}o+=5./i;
}
