float angle = 70.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv;

    // 
    uv.x = -(fragCoord.x)/(iResolution.x/2.) + 1.0;
    uv.y = -(fragCoord.y)/(iResolution.y/2.) + 1.0;

    vec3 col = vec3(0.1, 0.,0.0);

    float y =   (
                   (abs((uv.x)*4.) * cos(angle) 
                   + (uv.y/1.3+0.2) * sin(angle))
                   *
                   (abs(uv.x*4.) * cos(angle) 
                   + (2.1*uv.y*uv.y-1.4) * sin(angle))
                  )
                 /
                 .3
                 + .5 + sin(iTime*14.)/8.5;

    if (uv.y >= y)
        col = vec3(smoothstep(1.2*y, 1.2, 0.4), 0. ,0.1-sin(iTime*14.)/5.5);

    fragColor = vec4(col,1.0);
}
