#define R3D(a,A) mix(A*dot(p,A), p, cos(a)) + sin(a)*cross(p,A)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.2,e; vec3 p,q=vec3(.86,.5,.57), r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)*r.y,e)/64.;
    p=R3D(iTime*.8,q);

    for(j=0.;j++<8.;)
        p=abs(p=R3D(.5,vec3(-1))),
        d=max(min(p.x,p.y),q.z);
        d-=dot(p,p)/2e4;

    e+=d;
}o+=vec4(sqrt(p),1)/i;
}
