#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(-1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.z-=2.;
    p.xz*=R2D(i-t*.3),p.zy*=R2D(i+t*.3);

    for(j=0.;j++<4.;)
        p=abs(abs(p-.1)-.04)-.03,
        d=max(min(p.y,p.z),p.x),
        d-=dot(p,q)/1e4;

    e+=d;   
}o+=vec4(exp(p*i*e),1)*3./i;
}
