// Fork of "Deposits" by wyatt. https://shadertoy.com/view/4c2cDK
// 2024-08-08 21:46:17

Main {
    Q = sin(sign(B(U).z-.5*R.x)+B(U).w/R.y+vec4(1,2,3,4));
}

Main {

    Q = A(U);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));

    Q.xy -= .5*vec2(e.z-w.z,n.z-s.z);
    vec4 C  =  vec4(sign(B(U).z-.5*R.x));//C(B(U).zw)*2.-1.;
    Q.y -= .1/R.y*C.z;
    for (float x = -4.; x <= 4.; x++)
    for (float y = -4.; y <= 4.; y++)
    {
        vec2 u = vec2(x,y);
        vec4 q = B(U+u);
        vec4 c = vec4(sign(q.z-.5*R.x));//C(q.zw)*2.-1.;
        vec2 r = (U-q.xy);
        float l = length(r);
        if (l>0.)
        Q.xy -=.0001*r/l*exp(-.05*dot(U-q.xy,U-q.xy))*sign(c.z)*sign(C.z);
    }
    Q.xy *= .9995;
    if (U.x < 5.||R.x-U.x<5.||U.y < 5.||R.y-U.y<5.)Q.xy *= 0.;

}

Main {

    Q = A(U);
    vec4 n = A(U+vec2(0,1));
    vec4 e = A(U+vec2(1,0));
    vec4 s = A(U-vec2(0,1));
    vec4 w = A(U-vec2(1,0));

    Q.z -= .5*(e.x-w.x+n.y-s.y);

}

Main {
    if (iFrame%1==0) {
        U -= .5*A(U).xy;
        U -= .5*A(U).xy;
    }
    Q = A(U);
}

Main {
    Q = vec4(-1);
    for (float x = -2.; x <= 2.; x++)
    for (float y = -2.; y <= 2.; y++)
    {
        vec2 u = vec2(x,y);
        vec4 q = B(U+u);
        if (length(U-q.xy)<length(U-Q.xy)) {

            Q = q;

        }
    }

    Q.xy += A(U).xy;
    //Q.zw = mix(Q.zw,B(Q.xy-A(U).xy).zw,.5);

    if (iFrame < 1) Q = vec4(U,U);
}
