vec3 palette( float t ) {
    // default mode
    vec3 a = vec3(1.0, 1.0  , 1.0);
    vec3 b = vec3(0.5, 0.5  , 0.5);
    vec3 c = vec3(1.0, 1.0  , 1.0);
    vec3 d = vec3(0.0, 0.333, 0.667);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    uv += vec2(0.125*sin(iTime),0.125*sin(sin(iTime)+iTime));

    float d = sin((length(uv)*log(iTime+1.0))*32.0);

    vec3 col = log(vec3(0.4/d)+1.0)/log(2.0);

    for(float i = 0.0;i<4.0;i++){
        float i0 = mod(i,2.0)-1.0;

        uv *= 2.0;

        col *= palette(iTime+length(uv/4.0)*i0);
    }

    fragColor = vec4(col,1.0);
}
