// ref: https://www.shadertoy.com/view/XtfGzN
#define Iterations 40

float getJulia(vec2 uv, int iter, float time, float seuilInf, float seuilSup)
{
    float lX = -0.74;
    float lY = time*0.11;
    float julia = 0., x = 0., y = 0., j=0.;
	for(int i=0; i<Iterations; i++) 
    {
        if ( i == iter ) break;
        x = (uv.x * uv.x - uv.y * uv.y) + lX;
        y = (uv.y * uv.x + uv.x * uv.y) + lY;
        uv.x = x;
        uv.y = y;
       	j = mix(julia, length(uv)/dot(x,y), 0.6);
        julia = j;
    }
    return julia;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // vars / time
    float t1 = sin(iTime);
    float t2 = 0.5*t1+0.5;
    float t3 = 0.5*sin(iTime*0.1)+0.5;

    // uv
    float ratio = iResolution.x/iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.xy*2.-1.;
    uv.x*=ratio;

    // julia
    float Time = t1;// t2,t3
    int nIter = int(floor(float(Iterations)));
    float julia = getJulia(uv, nIter, Time, 0.1, 6.6); 

    // color
    float d0 = julia;
    float d = smoothstep(d0-5.,d0+4.,1.);
    float r = mix(1./d, d, 1.);
    float g = mix(1./d, d, 3.);
    float b = mix(1./d, d, 5.);
    vec3 color = vec3(r,g,b);

    fragColor.rgb = color;
}
