void mainImage( out vec4 fragColor, in vec2 fragCoord )  
{  
    // 将坐标转换为标准化设备坐标  
    vec2 uv = fragCoord / iResolution.xy;  // 0.0到1.0之间  
    uv = uv * 2.0 - 1.0; // 转换到-1.0到1.0之间  
    uv.x *= iResolution.x / iResolution.y; // 维持纵横比  

    // 计算圆环的参数  
    float radius = 0.5;     // 圆环的内半径  
    float thickness = 0.1;  // 圆环的宽度  

    // 计算当前像素到圆心的距离  
    float dist = length(uv); // 圆心在 (0,0)  

    // 判断像素是否在圆环内  
    float ring = smoothstep(radius, radius + thickness, dist) - smoothstep(radius + thickness, radius + 2.0 * thickness, dist);  

    // 根据是否在圆环内来设置颜色  
    fragColor = vec4(vec3(ring), 1.0); // 使用白色表示圆环部分，透明度为1.0  
}
