void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //gradient
    vec3 blue = vec3(0.0, 0.0, 1.0);
    vec3 teal = vec3(0.0, 1.0, 0.75); 
    vec3 gradient = mix(teal, blue, (uv.x + uv.y) - 0.9);

    //grid for squares
    vec2 uvcSquares = fract(uv * 10.0);
    uvcSquares -= 0.5;
    uvcSquares.x *= iResolution.x / iResolution.y;

    //draw squares
    float maxDist = max(abs(uvcSquares.x), abs(uvcSquares.y));
    float square = step(maxDist, 0.25); 

    vec3 color = mix(gradient, vec3(1.0), square);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
