#define Main void mainImage(out vec4 Q, in vec2 U)
#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec4 hash(vec4 p4)
{
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

float ln (vec3 u, vec3 a, vec3 b) {

    return length(u-a-(b-a)*clamp(dot(u-a,b-a)/dot(b-a,b-a),0.,1.));

}
#define INIT U = 2.*(U-.5*R)/R.y;\
    vec3 p = vec3(0,0,-1.);\
    vec3 d = normalize(vec3(U,3.5));\
    p.y += .3;\
    p.yz *= ei(1.2);\
    d.yz *= ei(1.2);\
    p.xy *= ei(.8);\
    d.xy *= ei(.8);\

float map (vec3 p) {
    float d = -p.z+0.01;

    for (float i = 0.; i < 16.; i++) {
        d = min(d,ln(p,vec3(0),vec3(0,0,-.18*exp2(-.45*i)))-.01*exp2(-.4*i));

        p.z -= -.18*exp2(-.45*i);
        p.x = abs(p.x);
        p.xz *= ei(.75);
        p.xy *= ei(1.5*sin(i));
        p.z = -abs(p.z);
    }
    return d;

}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-3,0);
    float m = map(p);
    return normalize(vec3(
        map(p+e.xyy)-m,
        map(p+e.yxy)-m,
        map(p+e.yyx)-m
    ));

}

Main {
    if (iFrame > 1) {Q = A(U);return;}
    INIT
    vec3 p0 = p;
    for (float i = 0.; i < 100.; i++) {
        p += d*map(p);
    }
    Q = vec4(normal(p),length(p-p0));

}

Main {
    vec4 h = hash(vec4(U,iFrame,iTime));
    vec4 a = A(U);
    Q = B(U);
    INIT
    vec3 light = vec3(0,-.09,-.45);
    p = p + d*h.x*a.w;
    h = hash(vec4(round(p*500.),1));
    d = normalize(light-p);

    for (float i = 0.; i < 50.; i++) {
        p += d*min(map(p),length(p-light)-.001);
    }
    float il = exp(-500.*(length(p-light)-.001));
    Q += vec4(1)*(il-.2)*(1.+60.*pow(il*h.w,500.))/(.1+2000.*length(p-light));


}
