const float brush_size = 100.0;
const float pi= 3.14159265358979323846264338327950288;
const float sample_dist = 2.;
const float sample_count =8.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord )/iResolution.xy;
    vec4 buff_sample = texture(iChannel0, uv);

    vec2 l = buff_sample.xy;
    vec2 v = buff_sample.zw;

    vec2 m = iMouse.xy;
    float brush = max(brush_size - length(fragCoord - m),0.0);

    v.x += brush*sin(iTime);
    v.y += brush*cos(iTime);


    l+=v;



    if (length(l)>1.0)
    {
    vec2 ln = l/length(l);
    v -=  1.9 * ln * dot(ln,v);

    }


    for (float a = 0.0; a < pi*2.0; a += pi*2.0 / float( sample_count))
    {

        vec2 sample_offset = vec2(cos(a),sin(a));
        vec2 sampleuv = sample_offset*sample_dist + uv;
        vec4 sampl =  texture(iChannel0,sampleuv).xyzw;

        vec2 particle_loc  = sampleuv + uv + sampl.xy + sampl.zw;

        if (length(particle_loc - uv) < 5.){
        v+= sampl.zw;
        }
      }



    fragColor = vec4(l,v);
}
