vec3 drawCircle(vec2 CenterPos,vec2 circlePos,float radio,vec3 showCol)
{
    vec3 col = vec3(0,0,0);

    float dis = distance(CenterPos,circlePos) - radio;

    col = dis > 0.0 ? col : showCol;

    return col;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //统一UV
    vec2 uv = fragCoord/iResolution.xy;

    //设置圆心为中心点
    uv -= 0.5;

    //统一UV比例
    uv.x *= iResolution.x/iResolution.y;

    //vec3 col1 = drawCircle(uv,vec2(0,0),0.2,vec3(1,0,0));

    vec3 col2 = drawCircle(uv,vec2(-0.3,0.0),0.2,vec3(0,1,0));

    // Output to screen
    fragColor = vec4(col2,1.0);
}
