void mainImage( out vec4 O, vec2 U )
{
    O = T(U);
    float  m = O.r/O.a, s = O.b, c = s/m;     // mean, std-dev, autocorrelation
    O = vec4( sqrt(m), sqrt(s), sqrt(c), 1);  // to sRGB
  //O = O.zzzz;                               // display autocorrelation only
}

void mainImage( out vec4 O, vec2 U )
{
    O.r = T(U).a;
    O.g = O.r*O.r;
    O.a = 1.;
}

void mainImage( out vec4 O, vec2 U )
{
    U.x = R.x-U.x;           // video = mirror
    O   =  T(U);
    O.a = dot(O.rgb,O.rgb);  // sRGB to flat
}

// --- compute temporal moments M1,M2 (.rg), and std-dev (.b) 
//     .a = normalization -> mean = .r/.a

void mainImage( out vec4 O, vec2 U )
{
    O = T2(U);
  //b = 2.;                                    // use LOD input 
  //relax = mix(40., 2., clamp(4.*O.b,0.,1.)); // adaptive integration
    O = mix( O , T(U), 1./relax );             // relaxation

    float m =               O.r/O.a,           // temporal mean 
          s = sqrt( max(0., O.g/O.a - m*m ) ); // temporal std dev
    O.b = s;
}
