vec3 sdfCircle(vec2 uv, float r, vec2 offset){

float x = uv.x - offset.x;

float y = uv.y - offset.y;

float d = length(vec2(x,y)) - r;

return d > 0. ? vec3 (2.) : 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0.145,0.616,0.475));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord / iResolution.xy;

uv = uv-0.6;

uv = uv*3.0;

uv.x *= iResolution.x / iResolution.y;

float rings = length(uv / 5.0);

rings = sin(rings*4. - cos(iTime)) / 100.0;

rings = abs(rings);

rings = smoothstep(0.0,0.13, rings);

rings = 0.005 / rings;

vec2 offset = vec2(sin(iTime*14.)*0.8, cos(iTime*14.)*0.5);

vec3 col = rings / sdfCircle(uv, 5.0, offset);

fragColor = vec4 (col,1.0);

}
