void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize coordinates to the center of the screen
    vec2 uv = (fragCoord - 0.6 * iResolution.xy) / iResolution.y;

    // Parameters for the sequence
    float n = uv.x * 5.0;  // Horizontal coordinate influences 'n'
    float x = uv.y * 5.0;  // Vertical coordinate influences 'x'
    float sequence = 1.0;  // Starting value for the sequence

    // Iterate based on some fractal-like rules
    for(int i = 0; i < 20; i++) {
        x = x + 1.0;
        sequence = (4.0 + 3.0 * n) * pow(4.0, x);
        sequence /= 3.0;  // Apply your sequence formula
        if(length(vec2(sequence, uv.y)) <500.0) break;  // Escape condition
    }

    // Use the logarithm of the sequence to colorize, modulated smoothly
    float color = log(abs(sequence) + 1.0);  // Simplify color calculations

    // Define a base color modification that varies slowly over time
    float baseColorMod = 0.5 + 0.5 * sin(iTime * 0.1);  // Slow time variation
    vec3 col2 =0.03* vec3(1.5 * sin(color*iTime*0.5), 0.5*x * cos(color*x*iTime*0.5) + 0.5,1.4* sin(color*x*iTime*0.2) *x*6.0* cos(0.1*iTime));

    // Calculate individual color channels with gentle oscillation
    vec3 col;
    col.r = 0.5 * sin(color + iTime * 0.5) + 0.5;
    col.g = 0.5 * cos(color + iTime * 0.5) + 0.1;
    col.b = 0.5 * sin(color + iTime * 2.5) + 1.5;

    // Adjust overall color brightness and contrast
    col = col * baseColorMod *abs(log((col2)));  // Apply base color modulation

    // Output to screen
    fragColor = vec4(col, 1.0);
}
