float random (in vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

vec2 warpCoords(vec2 uv, float fact) {
    return uv / (1.0 + fact * (1.0 - uv.y*0.8));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tileCount = 10.;
    vec2 uv = fragCoord/iResolution.y;
    vec2 suv = fragCoord/iResolution.xy;
    uv.x -= (iResolution.x/2.)/iResolution.y;
    uv.y -= (iResolution.y/2.)/iResolution.y;

    vec3 col = vec3(0.4, 0., sin(iTime)*0.4);

    for (int i = 0; i < 20; i++) {
        // WARPED uv
        float fact = 0.05;
        vec2 wuv = warpCoords(uv, pow(float(i)*fact, 2.));
        wuv.x -= iTime*0.1;
        vec2 t = floor(wuv*tileCount);

        float theta = float(i)*0.5+iTime*2.*random(t+2.);
        wuv.x -= sin(theta)*0.01;
        wuv.y -= cos(theta)*0.01;
        t = floor(wuv*tileCount);
        vec2 tuv = fract(wuv*tileCount);

        float size = 0.4-float(i)*0.02;

        if (length(tuv-vec2(0.5)) < size) {
            col = vec3(size, float(i)/20., sin(float(i)*0.2+iTime)*0.4);
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
