const vec3  eye      = vec3 ( 0, 0, 3 );
const vec3  light    = vec3  ( 0.0, 3.0, 5.0 );
const vec3  lightGreen = vec3 ( 0.0, 0.0, 2.0 );
const vec3  lightRed = vec3 (0.0, 3.0, 1.0);
const vec3  lightBlue = vec3 (0.0, 0.0, 0.0);
const int   maxSteps = 50;
const float eps      = 0.01;

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

float length8 ( in vec2 p )
{
    return pow ( pow ( p.x, 6.0 ) + pow ( p.y, 6.0 ), 1.0/ 8.0 );
}

float length8 ( in vec3 p )
{
    return pow ( pow ( p.x, 8.0 ) + pow ( p.y, 8.0 ) + pow ( p.z, 8.0 ), 1.0/ 8.0 );
}

float dTorus ( vec3 p, vec2 t )
{
	vec2	q = vec2 ( length8 ( p.xz ) - t.x, p.y);
	return length8 ( q ) - t.y;
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b + r;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float smin ( float a, float b, float k )
{
	float res = exp ( -k*a ) + exp ( -k*b );
	return -log ( res ) / k;
}

float sdf ( in vec3 p, in mat3 m, out bool isRoundBox )
{
   vec3 q = m * p;
   float roundBoxDist = sdRoundBox(q, vec3 ( 0.5, 0.5, 0.8), 1.0);
   float torusDist = dTorus ( q, vec2 ( 1.0, 0.3 ) );
   isRoundBox = roundBoxDist < torusDist;

   return smin(roundBoxDist, torusDist, 10.0);
}

float sdf ( in vec3 p, in mat3 m )
{
   vec3 q = m * p;
   return smin(sdRoundBox(q, vec3 ( 0.5, 0.5, 0.8), 1.0), dTorus ( q, vec2 ( 1.0, 0.3 ) ), 10.0);
}

vec3 trace ( in vec3 from, in vec3 dir, out bool hit, in mat3 m, out bool isRoundBox )
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;

	for ( int steps = 0; steps < maxSteps; steps++ )
	{
		float	dist = sdf ( p, m, isRoundBox );

		if ( dist < 0.01 )
		{
			hit = true;
			break;
		}

		totalDist += dist;

		if ( totalDist > 10.0 )
			break;

		p += dist * dir;
	}

	return p;
}

vec3 generateNormal ( vec3 z, float d, in mat3 m )
{
    float e   = max (d * 0.5, eps );
    float dx1 = sdf(z + vec3(e, 0, 0), m);
    float dx2 = sdf(z - vec3(e, 0, 0), m);
    float dy1 = sdf(z + vec3(0, e, 0), m);
    float dy2 = sdf(z - vec3(0, e, 0), m);
    float dz1 = sdf(z + vec3(0, 0, e), m);
    float dz2 = sdf(z - vec3(0, 0, e), m);

    return normalize ( vec3 ( dx1 - dx2, dy1 - dy2, dz1 - dz2 ) );
}

const float roughness = 0.2;
const vec3  r00   = vec3 ( 1.0, 0.92, 0.23 );
const vec3  r01   = vec3 ( 0.7, 0.7, 0.7 );
const float gamma = 2.2;
const float pi    = 3.1415926;
const float FDiel = 0.04;

vec3 fresnel ( in vec3 f0, in float product )
{
	product = clamp ( product, 0.0, 1.0 );

	return mix ( f0, vec3 (1.0), pow(1.0 - product, 5.0) );
}

float D_beckmann ( in float roughness, in float NdH )
{
	float m    = roughness * roughness;
	float m2   = m * m;
	float NdH2 = NdH * NdH;

	return exp( (NdH2 - 1.0) / (m2 * NdH2) ) / (pi * m2 * NdH2 * NdH2);
}

float G_default ( in float nl, in float nh, in float nv, in float vh )
{
	return min ( 1.0, min ( 2.0*nh*nv/vh, 2.0*nh*nl/vh ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    bool hit;
	bool isRoundBox;
	vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);
    mat3 m     = rotateX ( 6.0*mouse.y ) * rotateY ( 6.0*mouse.x);
    vec2 scale = 9.0 * iResolution.xy / max ( iResolution.x, iResolution.y ) ;
    vec2 uv    = scale * ( fragCoord/iResolution.xy - vec2 ( 0.5 ) );
	vec3 dir   = normalize ( vec3 ( uv, 0 ) - eye );
    vec4 color = vec4 ( 0, 0, 0, 1 );
    vec3 p     = trace ( eye, dir, hit, m, isRoundBox );

	if ( hit )
	{
		vec3  l  = normalize        ( light - p );
        vec3  lg = normalize        ( lightGreen - p );
        vec3  lr = normalize        ( lightRed - p );
        vec3  lb = normalize        ( lightBlue - p );
        vec3  v  = normalize        ( eye - p );
		vec3  n  = generateNormal   ( p, 0.001, m );
		float nl = max ( 0.0, dot ( n, l ) );
        float nlGreen = max(0.0, dot(n, lg));
        float nlRed = max(0.0, dot(n, lr));
        float nlBlue = max(0.0, dot(n, lb));
        vec3  h  = normalize ( l + v );
        float hn = max ( 0.0, dot ( h, n ) );
        float sp = pow ( hn, 150.0 );

        if (isRoundBox) {
            color += vec4(vec3(lg), 1.0);
            color += vec4(vec3(lr), 1.0);
            color += vec4(vec3(lb), 1.0);
        } else {
            color = vec4(vec3(l), 1.0);
            color.rgb += nlGreen * vec3(0.0, 0.6, 0.0);
            color.rgb += nlRed * vec3(0.6, 0.0, 0.0);
            //color.rgb += nlBlue * vec3(0.0, 0.0, 0.6);
        }
	} 

    fragColor = color;
}
