// Hi there, dear galaxy of ours. Made by Rik Riesmeijer, 2024: CC0, have fun :)
float n(vec2 v) { uvec2 u=uvec2(v*3e4+v.y);return vec4(u.yx*=u^=u*u,u).x/43e8; }
vec4 s(vec2 v, float d) { vec4 i,s;for(;i.x++*d<1.0;v.x++)s+=d*n(v); return s; }
vec4 t(vec2 v, float l, float h) { return smoothstep(l, h, s(v/1e4+3e1,0.01)); }
vec4 g(vec2 v) { return t(v, 0.439, .2 + sin(iTime * n(v) + sin(iTime)) * .2); }
void mainImage(out vec4 c, vec2 v) { c = pow(g(v + v), vec4(.231, 1, .11, 1)); }
// n is monochrome noise, s is n integral with du named d, others animate color.
