void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0.0, 0.0, 0.0);

    vec3 red = vec3(1.0, 0.0, 0.0);
    vec3 blue = vec3(0.0, 0.0, 1.0);
    vec3 white = vec3(1.0, 1.0, 1.0);

    vec3 mixCol = mix(red, blue, uv.x);
    vec3 lerpCol = mix(red, blue, smoothstep(0.0, 1.0, uv.x));
    vec3 stepCol = mix(red, blue, step(0.5, uv.x));

    if (uv.y >= 0.67) {
        col = stepCol;
    } else if (uv.y >= 0.33) {    
        col = mixCol;
    } else if (uv.y >= 0.00) {
        col = lerpCol;
    }

    vec3 topLine = smoothstep(0.0, 0.005, abs(vec3(uv.y - 0.67)));
    vec3 bottomLine = smoothstep(0.0, 0.005, abs(vec3(uv.y - 0.33)));
    vec3 mixLine = smoothstep(0.0, 0.005, abs(vec3(uv.y - mix(0.33, 0.67, uv.x))));
    vec3 lerpLine = smoothstep(0.0, 0.005, abs(vec3(uv.y - mix(0.0, 0.33, smoothstep(0.0, 1.0, uv.x)))));
    vec3 stepLine = smoothstep(0.0, 0.005, abs(vec3(uv.y - mix(0.67, 1.0, smoothstep(0.495, 0.505, uv.x)))));

    col = mix(white, col, topLine);
    col = mix(white, col, bottomLine);
    col = mix(white, col, mixLine);
    col = mix(white, col, lerpLine);
    col = mix(white, col, stepLine);

    fragColor = vec4(col,1.0);
}
