void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 rgb = texture(iChannel0, uv).rgb;
    float temp = 0.0;

    temp += rgb.r;
    temp += rgb.g;
    temp += rgb.b;
    temp /= 3.0;


    rgb.r = temp;
    rgb.g = temp;
    rgb.b = temp;

    fragColor = vec4(rgb, 1.0); // export to screen
}
