float _Offset = 0.1;
float _Area = 1.2;
float _Spread = 0.8;

float TiltShiftMask(vec2 uv)
{
    float centerY = uv.y * 2.0 - 1.0 + _Offset; // [0,1] -> [-1,1]
    return pow(abs(centerY * _Area), _Spread);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float mask = TiltShiftMask(uv);

    vec3 col1 = texture(iChannel0, uv).rgb;
   	vec3 col2 = texture(iChannel1, uv).rgb;
    vec3 col = mix(col2,col1,mask);
    // Output to screen
    fragColor = vec4(col,1.0);
}

const float R = 12.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord / iResolution.xy;
	vec3 sum_c = texture(iChannel0, uv).rgb;
   	vec2 offset = vec2(0.0,1.0 / iResolution.y);
    for(float i = 1.5; i <= R; i+=2.0){
    	sum_c += texture(iChannel0, uv + offset * i).rgb * 2.0;
        sum_c += texture(iChannel0, uv - offset * i).rgb * 2.0;
    }
    fragColor = vec4(sum_c / (2.0 * R + 1.0), 1.0);
}

