vec3 ring(vec2 fragCoord){
     vec2 uv=fragCoord/iResolution.xy*2.0-1.0;
    uv.x*=iResolution.x/iResolution.y;
    float d=length(uv);
    vec3 color=vec3(1.0,2.0,3.0);
    d=sin(d*4.0-iTime)/4.;
    d=abs(d);
    d=0.005/d;
    color*=d;
    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 fragcoord = vec2(fragCoord.xy / iResolution.xy);
    vec3 bgColor = vec3(0,0,0);
    vec3 pixelColor = bgColor;
    vec3 gridColor = vec3(0.5,0.8,1.0);

    const float width = 0.1;
    const float minWidth = 0.003;
    for(float i = 0.0; i < 1.0; i+=width){
    if (mod(fragcoord.x,width) < minWidth || mod(fragcoord.y,width) < minWidth){
            pixelColor = gridColor;
        }
    }
    fragColor = vec4(pixelColor*ring(fragCoord),1.0);
}
