void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Fake anaglyph
    vec4 left = texture(iChannel0, uv - vec2(0.05, 0));
    vec4 right = texture(iChannel0, uv + vec2(0.05, 0));

    // Just copy the right's blue channel
    fragColor = vec4(left.r, left.g, right.b, left.a);
}
